query GetEventAnalytics($calendarId: ID, $limit: Int = 10) {
  eventAnalytics(calendarId: $calendarId, limit: $limit) {
    totalEvents
    totalAttendees
    averageAttendeesPerEvent
    checkInRate
    events {
      id
      name
      date
      attendeeCount
      checkedInCount
      location
    }
  }
}
